local TweenService=game:GetService("TweenService")local UserInputService=game:GetService("UserInputService")local RunService=game:GetService("RunService")local openButton={}local Defaults={ImageId=0,Size=UDim2.new(0,50,0,50),Position=UDim2.new(0,15,0.5,-25),BackgroundColor=Color3.fromRGB(30,30,30),BackgroundTransparency=0.15,CornerRadius=12,KeepInBounds=true,ZIndex=10}local function Tween(o,i,g)local t=TweenService:Create(o,i,g)t:Play()return t end local function Rainbow()local t=tick()*0.5 return Color3.fromHSV(t%1,1,1)end function openButton.CreateDragButton(cfg)local c={}for k,v in pairs(Defaults)do c[k]=cfg[k]or v end local PlayerGui=game.Players.LocalPlayer:WaitForChild("PlayerGui")local gui=Instance.new("ScreenGui")gui.Name="OpenButtonUI"gui.ResetOnSpawn=false gui.IgnoreGuiInset=true gui.ZIndexBehavior=Enum.ZIndexBehavior.Sibling gui.Parent=PlayerGui local btn=Instance.new("ImageButton")btn.Size=c.Size btn.Position=c.Position btn.BackgroundColor3=c.BackgroundColor btn.BackgroundTransparency=c.BackgroundTransparency btn.Image="rbxassetid://"..tostring(c.ImageId)btn.ZIndex=c.ZIndex btn.Parent=gui btn.Active=true btn.Draggable=false btn.AutoButtonColor=false local corner=Instance.new("UICorner",btn)corner.CornerRadius=UDim.new(0,c.CornerRadius)local stroke=Instance.new("UIStroke",btn)stroke.Thickness=2.5 stroke.Color=Color3.fromRGB(255,255,255)stroke.Transparency=0.1 task.spawn(function()while btn.Parent do stroke.Color=Rainbow()RunService.RenderStepped:Wait()end end)local dragging=false local dragInput=nil local dragStart=Vector2.new(0,0) local startAbs=Vector2.new(0,0) btn.InputBegan:Connect(function(input)if input.UserInputType==Enum.UserInputType.MouseButton1 or input.UserInputType==Enum.UserInputType.Touch then dragging=true dragInput=input dragStart=input.Position startAbs=btn.AbsolutePosition input.Changed:Connect(function()if input.UserInputState==Enum.UserInputState.End then dragging=false dragInput=nil end end)end end)UserInputService.InputChanged:Connect(function(input)if not dragging or not dragInput then return end if input~=dragInput then return end if input.UserInputType==Enum.UserInputType.MouseMovement or input.UserInputType==Enum.UserInputType.Touch then local delta=input.Position-dragStart local newX=startAbs.X+delta.X local newY=startAbs.Y+delta.Y if c.KeepInBounds then local s=workspace.CurrentCamera.ViewportSize local size=btn.AbsoluteSize newX=math.clamp(newX,0,s.X-size.X) newY=math.clamp(newY,0,s.Y-size.Y) end btn.Position=UDim2.fromOffset(math.floor(newX),math.floor(newY)) end end)function c:show()btn.Visible=true Tween(btn,TweenInfo.new(0.25,Enum.EasingStyle.Back,Enum.EasingDirection.Out),{Size=c.Size,ImageTransparency=0})end function c:hide()Tween(btn,TweenInfo.new(0.2,Enum.EasingStyle.Quad,Enum.EasingDirection.In),{Size=UDim2.new(0,0,0,0),ImageTransparency=1})task.wait(0.2)btn.Visible=false end function c:setImage(id)btn.Image="rbxassetid://"..tostring(id)end function c:setPos(pos)btn.Position=pos end function c:setColor(col)btn.BackgroundColor3=col end function c:destroy()gui:Destroy()end function c:pulse()local s=btn.Size Tween(btn,TweenInfo.new(0.09,Enum.EasingStyle.Back,Enum.EasingDirection.Out),{Size=UDim2.new(s.X.Scale,math.floor(s.X.Offset*1.12),s.Y.Scale,math.floor(s.Y.Offset*1.12))}).Completed:Connect(function()Tween(btn,TweenInfo.new(0.12,Enum.EasingStyle.Back,Enum.EasingDirection.Out),{Size=s})end)end c.Button=btn c.Gui=gui return c end return openButton
